    {
        foreach (string item in array)
        {
            if (item == cellString)
            {
                return true;
            }
        }

        XlsxDebug.LogErrorFormat("Table {0} : Type Must Be {1} ({2}) ", mProtoName, string.Join(",", array), cellString);

