    private string _getProtoEnumString(string name, List<ProtoXlsxEnumItem> list)
    {
        string enumStr = "enum " + name + "\n";

        enumStr += "{\n";
        foreach (var item in list)
        {
            // key = value; // comment
            enumStr += string.Format("\t{0} = {1}; // {2}\n", item.key, item.value, item.comment);
        }
        enumStr += "}\n";

        return enumStr;
    }

